mapper.select:
SELECT id, memberId, password, nickName, createTime, email, address, phone
FROM member
WHERE id = #{id};

SELECT id, memberId, password, nickName, createTime, email, address, phone
FROM member
WHERE memberId = #{memberId};

SELECT COUNT(memberId)
FROM member
WHERE memberId = #{memberId}

SELECT id, memberId, password, nickName, createTime, email, address, phone
FROM member
WHERE memberId = #{memberId}
AND password = #{password}
