mapper.select:
SELECT memberId, password, nickName, createTime, isWithDraw, status
FROM member
WHERE memberId = #{id}

mapper.update:
UPDATE member
SET password=#{password},
nickName=#{nickName},
isWithDraw=#{isWithDraw},
status=#{status}
WHERE memberId = #{memberId}

mapper.select:
SELECT id, memberId, password, nickName, createTime, isWithDraw, status
FROM member
WHERE memberId = #{memberId}
AND password = #{password}
AND status != 'DELETE'

SELECT COUNT(id)
FROM member
WHERE memberId = #{memberId}

mapper.update:
UPDATE member
SET password = #{password}
WHERE memberId = #{memberId}
